// UpdateProfile.js 

import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { userUpdateProfile } from "../../redux/UserSlice";
import UserService from "../../services/UserService";

const UpdateProfile = () => {
    console.log('UpdateProfile');
    const dispatch = useDispatch();
    const userData = useSelector((state) => state.user.loggedInUser);
    const token = useSelector(store => store.user.jwtToken);
    console.log(userData);
    const [formData, setFormData] = useState(userData);

    const handleChange = (evt) => {
        console.log(evt.target);
        console.log(formData);
        setFormData({
            ...formData,
            [evt.target.name]: evt.target.value
        });
    };

    const handleSubmit = async (evt) => {
        console.log(formData);
        evt.preventDefault();
        try {
            const user = await UserService.updateUserProfile(formData, token);
            console.log(user);
            dispatch(userUpdateProfile(user));
        }
        catch (error) {
            console.log(error);
            if (error.code === 'ERR_BAD_REQUEST')
                alert(error.message);
        }
    };

    return (
        <>
            <h1>Update Your Profile</h1>

            <form onSubmit={handleSubmit}>

                <div class="input-group mb-3" style={{ width: '50rem' }}>
                    <div class="input-group-prepend">
                        <span class="input-group-text" id="inputGroup-sizing-default">First Name</span>
                    </div>
                    <input type="text" class="form-control" name="firstName" value={formData.firstName} onChange={handleChange} aria-label="Sizing example input" aria-describedby="inputGroup-sizing-default" />
                </div>

                <div class="input-group mb-3" style={{ width: '50rem' }}>
                    <div class="input-group-prepend">
                        <span class="input-group-text" id="inputGroup-sizing-default">Last Name</span>
                    </div>
                    <input type="text" class="form-control" name="lastName" value={formData.lastName} onChange={handleChange} aria-label="Sizing example input" aria-describedby="inputGroup-sizing-default" />
                </div>

                <div class="input-group mb-3" style={{ width: '50rem' }}>
                    <div class="input-group-prepend">
                        <span class="input-group-text" id="inputGroup-sizing-default">Phone</span>
                    </div>
                    <input type="number" class="form-control" name="firstName" value={formData.firstName} onChange={handleChange} aria-label="Sizing example input" aria-describedby="inputGroup-sizing-default" />
                </div>

                <label>Phone:</label>
                <input type="number" name="phone" value={formData.phone}
                    onChange={handleChange} required />
                <br />
                <label>Email:</label>
                <input type="email" name="email" value={formData.email}
                    onChange={handleChange} required />
                <br />
                <label>Avatar:</label>
                <input type="text" name="avatar" value={formData.avatar}
                    onChange={handleChange} />
                <br />
                <button type="submit">Update Your Profile</button>
            </form>





        </>
    );
};

export default UpdateProfile;
