import axios from "axios";
import { useState } from "react";

const AddEmployee = () => {
    const backendUrl = 'https://jsonplaceholder.typicode.com/users';
    const [empData, setEmpData] = useState({ firstName: '', email: '', aadhaar: '', salary: '' });
    const [errors, setErrors] = useState({});

    const handleChange = (evt) => {
        setEmpData({ ...empData, [evt.target.name]: evt.target.value });
        setErrors({ ...errors, [evt.target.name]: '' });
    };

    const validateForm = () => {
        let isValid = true;
        const newErrors = {};

        if (!empData.firstName.trim()) {
            newErrors.firstName = "First name is required";
            isValid = false;
        }

        if (!/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(empData.email)) {
            newErrors.email = "Invalid email address";
            isValid = false;
        }

        if (!/^\d{12}$/.test(empData.aadhaar)) {
            newErrors.aadhaar = "Aadhaar must be a 12-digit number";
            isValid = false;
        }

        if (empData.salary <= 0 || isNaN(empData.salary)) {
            newErrors.salary = "Salary must be a positive number";
            isValid = false;
        }

        setErrors(newErrors);
        return isValid;
    };

    const handleSubmit = (evt) => {
        evt.preventDefault();
        if (validateForm()) {
            axios.post(backendUrl, empData)
                .then((resp) => {
                    alert(`${resp.data.firstName} with id ${resp.data.id} added successfully!`);
                    setEmpData({ firstName: '', email: '', aadhaar: '', salary: '' });
                })
                .catch(error => {
                    console.error("Error adding employee:", error);
                });
        }
    };

    return (
        <>
        <div class="card" style={{ width: "50rem" }}>
                <div class="card-header" style={{ fontSize: '26px' }}>
                    <strong>Add Employee</strong>

                </div>




            <form onSubmit={handleSubmit}>
            <div class="form-group">
    <label htmlFor="firstName">First Name</label>
    <input type="text" className="form-control" id="firstName" value={empData.firstName} onChange={handleChange}/>
    {errors.firstName && <span className="error">{errors.firstName}</span>}
  </div>
  <div class="form-group">
    <label htmlFor="email">Email</label>
    <input type="email" class="form-control" id="firstName" value={empData.email} onChange={handleChange}/>
    {errors.email && <span className="error">{errors.email}</span>}
  </div>
  <div class="form-group">
    <label htmlFor="aadhar">Aadhar</label>
    <input type="number" class="form-control" id="aadhar" value={empData.aadhar} onChange={handleChange}/>
    {errors.aadhar && <span className="error">{errors.aadhar}</span>}
  </div>
  <div class="form-group">
    <label htmlFor="salary">First Name</label>
    <input type="text" class="form-control" id="firstName" value={empData.firstName} onChange={handleChange}/>
    {errors.firstName && <span className="error">{errors.firstName}</span>}
  </div>
                <label htmlFor="salary">Salary:</label>
                <input type="number" id="salary" name="salary" value={empData.salary} onChange={handleChange} placeholder="Enter salary" />
                {errors.salary && <span className="error">{errors.salary}</span>}
                <br />

                <button type="submit" class="btn btn-success">Submit</button>

            </form>
        </div>
        </>
    );
};

export default AddEmployee;
